components/products/description-tabs:
{{#if product.description}}
<section class="toggle">
  <h4 class="toggle-title">
      <a href="#tab-description" class="toggleLink is-open" data-collapsible>
        <svg class="icon--name"><use xlink:href="#icon-product-description"></use></svg>
        <span>{{lang 'products.description'}}</span>
        <svg class="toggle-down-arrow"><use xlink:href="#icon-keyboard-arrow-down"></use></svg>
      </a>
  </h4>
  <div class="toggle-content is-open" id="tab-description">
    {{{product.description}}}
  </div>
</section>
{{/if}}

{{#if product.custom_fields}}
    <section class="toggle">
      {{#if product.custom_fields}}
      <h4 class="toggle-title">
          <a href="#tab-custom_fields" class="toggleLink" data-collapsible>
            <svg class="icon--name"><use xlink:href="#icon-product-other-details"></use></svg>
            <span>{{lang 'products.other-details'}}</span>
            <svg class="toggle-down-arrow"><use xlink:href="#icon-keyboard-arrow-down"></use></svg>
          </a>
      </h4>
      {{/if}}
      {{#if product.custom_fields}}
        <div class="toggle-content clearfix" id="tab-custom_fields">
          {{#each product.custom_fields}}
              <dt class="productView-info-name">{{name}}:</dt>
              <dd class="productView-info-value">{{{value}}}</dd>
          {{/each}}
        </div>
      {{/if}}
    </section>
{{/if}}

{{#if product.warranty}}
    <section class="toggle">
      {{#if product.warranty}}
      <h4 class="toggle-title">
          <a href="#tab-warranty" class="toggleLink" data-collapsible>
            <svg class="icon--name"><use xlink:href="#icon-product-warranty"></use></svg>
            <span>{{lang 'products.warranty'}}</span>
            <svg class="toggle-down-arrow"><use xlink:href="#icon-keyboard-arrow-down"></use></svg>
          </a>
      </h4>
      {{/if}}
      {{#if product.warranty}}
        <div class="toggle-content" id="tab-warranty">
          {{{product.warranty}}}
        </div>
      {{/if}}
    </section>
{{/if}}

{{#if product.videos.list.length}}
        {{> components/products/videos product.videos}}
{{/if}}

{{#if theme_settings.show_product_reviews_tabs}}
  {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
{{/if}}

components/products/reviews:
{{#if reviews.total}}
<section class="toggle productReviews" id="product-reviews" data-product-reviews>
    <h4 class="toggle-title">
        {{#if theme_settings.show_product_reviews_tabs}}
            <a class="toggleLink" data-collapsible href="#productReviews-content">
              <svg class="icon--name"><use xlink:href="#icon-product-review"></use></svg>
              <span>{{lang 'products.reviews.review_header' total=reviews.total}}</span>
              <svg class="toggle-down-arrow"><use xlink:href="#icon-keyboard-arrow-down"></use></svg>
            </a>
        {{/if}}
    </h4>
    <div class="toggle-content" id="productReviews-content" aria-hidden="false">
        <ul class="productReviews-list" id="productReviews-list">
            {{#each reviews.list}}
            <li class="productReview">
                <article itemprop="review" itemscope itemtype="http://schema.org/Review">
                    <header>
                        <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="http://schema.org/Rating">
                            {{> components/products/ratings rating=rating}}
                            <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                        </span>
                        <h5 itemprop="name" class="productReview-title">{{{ title }}}</h5>
                        {{#if name}}
                            <meta itemprop="author" content="{{name}}">
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=name }}
                            </p>
                        {{else}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                        {{/if}}
                    </header>
                    <p itemprop="reviewBody" class="productReview-body">{{{ sanitize text }}}</p>
                </article>
            </li>
            {{/each}}
        </ul>
        {{> components/common/paginator pagination.reviews}}
    </div>
</section>
{{/if}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
